var openForm=document.querySelector(".button-open-form"),popup=document.querySelector(".modal-write-us"),close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),userName=popup.querySelector(".input-user-name"),userEmail=popup.querySelector(".input-user-email"),userText=popup.querySelector(".input-user-text");openForm.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),userName.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){userName.value&&userEmail.value&&userText.value||(e.preventDefault(),popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error"))});