var openMap=document.querySelector(".modal-map-link"),mapPopup=document.querySelector(".modal-map"),mapClose=mapPopup.querySelector(".modal-close");openMap.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.add("modal-show")}),mapClose.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&mapPopup.classList.contains("modal-show")&&mapPopup.classList.remove("modal-show")});